    if (empty($url) && !empty($data['route']) && $this->url_prefix) {
        $query = '';
        $route = $data['route'];

        unset($data['route']);

        foreach ($data as $key => $value) {
            $query .= '&' . rawurlencode((string)$key) . '=' . rawurlencode((is_array($value) ? http_build_query($value) : (string)$value));
        }

        $query = str_replace('&', '&amp;', trim($query, '&'));

        $link = $url_info['scheme'] . '://' . $url_info['host'] . (isset($url_info['port']) ? ':' . $url_info['port'] : '') . str_replace('/index.php', '', $url_info['path']) . '/' . $this->url_prefix . '?route=' . $route . $query;
    }
